mapper.select:
select *
  from (select a.*, rownum rn
          from (select schoolnumber,
                       name,
                       ${columnStr},
                       max(createtime) createtime
                  from (select b.*
                          from (select t1.schoolnumber,
                                       t1.name,
                                        to_number(${columnStr}) as ${columnStr},
                                       t.createtime
                                  from M_PEEVALUATION_DATA t
                                 inner join M_PEEVALUATION_STUDENTINFO t1
                                    on t.schoolnumber = t1.schoolnumber
                                 inner join M_PEEVALUATION_CLASSINFO t2
                                    on t1.classid = t2.id
                                 where t.datatype = ${dataType}
                                   and t1.classid = ${classId} 
                                 order by ${columnStr}) b)
                 group by schoolnumber, name, ${columnStr}
                 order by ${columnStr} ${orderKey} nulls last 
                 ) a)
 where rn >=${startRow} and rn <=${endRow}

select t.*
  from M_PEEVALUATION_STUDENTINFO t
 where t.schoolnumber = #{schoolNumber}

SELECT * FROM (SELECT b.*,rownum rn FROM ( select a.*,c.name as sclassname,d.name as sschoolname from M_PEEVALUATION_STUDENTINFO a left join M_PEEVALUATION_CLASSINFO c on a.classid = c.id left join M_PEEVALUATION_INSTITUTEINFO d on d.id = a.INSTITUTEID where 1=1
and a.name like '%'||#{name}||'%'
and schoolnumber like '%'||#{schoolNumber}||'%'
and gender = #{gender}
) b)
WHERE RN >=#{startRow} AND RN <=#{endRow}
and sclassname like '%'||#{sclassname}||'%'

		SELECT *
	  	FROM (SELECT b.*,rownum rn
	          FROM (
			 select a.*,c.name as stuName,d.name as className,d.teachername as teacherName,c.gender from M_PEEVALUATION_DATA a 
			left join m_Peevaluation_Studentinfo c on a.schoolnumber = c.SCHOOLNUMBER
      		left join m_Peevaluation_Classinfo d on d.id = c.classid
      		where a.DATATYPE = #{dataType}

    			and c.name like '%'||#{stuName}||'%'


    			and a.schoolnumber like '%'||#{schoolNumber}||'%'


    			and c.gender = #{gender}


			) b)

	 				WHERE RN >=#{startRow}  AND RN <=#{endRow} 


    			and className like '%'||#{className}||'%'

SELECT count(1) FROM (SELECT b.*,rownum rn FROM ( select a.*,c.name as stuName,d.name as className,d.teachername as teacherName from M_PEEVALUATION_DATA a left join m_Peevaluation_Studentinfo c on a.schoolnumber = c.SCHOOLNUMBER left join m_Peevaluation_Classinfo d on d.id = c.classid where a.DATATYPE = #{dataType}
and c.name like '%'||#{stuName}||'%'
and a.schoolnumber like '%'||#{schoolNumber}||'%'
and c.gender = #{gender}
) b)
and className like '%'||#{className}||'%'

select a.*,(select count(*) from m_peevaluation_studentinfo b left join m_peevaluation_classinfo c on b.classid = c.id where a.id = c.INSTITUTEID) as xyrs, (select count(*) from m_peevaluation_studentinfo b left join m_peevaluation_classinfo c on b.classid = c.id left join m_peevaluation_data d on b.schoolnumber = d.schoolnumber
where a.id = c.INSTITUTEID and d.datatype = #{dataType} and d.year = (Select to_char(sysdate,'yyyy') from dual) and d.generalscore >= 60
) as xyhgrs, (select count(*) from m_peevaluation_studentinfo b left join m_peevaluation_classinfo c on b.classid = c.id left join m_peevaluation_data d on b.schoolnumber = d.schoolnumber
where a.id = c.INSTITUTEID and d.datatype = #{dataType} and d.year = (Select to_char(sysdate,'yyyy') from dual) and d.generalscore >= 80
) as xyylrs from m_peevaluation_instituteinfo a
